INSERT INTO novel_station.novels_info_table (id, create_time, update_time, is_delete, title, description, watcher, words, collections, comments, chapters, is_end, image, author_info_id, novels_type_id, sales) VALUES (1, '2023-07-25 03:51:11.429292', '2023-07-26 13:11:30.784560', 0, '当昼与夜再次相遇', '这个平庸男人不平凡，徘徊在离婚边缘的时候，意外偶遇白富美初恋，对方还称一直爱慕着他。面对这样的诱惑，郑煜诚动摇了，并在奇幻老人的帮助下得到重置人生的机会，在全新的生活中，他果真迎娶了富家女宋珠铉。巧的是，他穿越前的泼妇妻子李承美也变成了与他朝夕相处的同事。', 321, 367799, 2342, 1, 129, 0, 'novels_image/3531896.jpg', 1, 3, 314);
INSERT INTO novel_station.novels_info_table (id, create_time, update_time, is_delete, title, description, watcher, words, collections, comments, chapters, is_end, image, author_info_id, novels_type_id, sales) VALUES (2, '2023-07-25 03:53:19.167947', '2023-07-26 13:46:04.049756', 0, '江湖似月牙', '仙人族在天王带领下走进人间，成了人人喊杀的妖人，遭遇灭族之灾。他穿越到元嘉年间，文采斐然，武功平平，成了一代妖王，肩负着仙人们复仇的全部希望。修炼绝世武功，消灭人族，建立仙人的世界？还是血债血偿惩罚制造者？他心存侠义仗道走天涯，演绎着现代谍战片的复仇传奇。', 426674, 102526, 31, 2, 39, 0, 'novels_image/3550854.jpg', 1, 2, 4314);
INSERT INTO novel_station.novels_info_table (id, create_time, update_time, is_delete, title, description, watcher, words, collections, comments, chapters, is_end, image, author_info_id, novels_type_id, sales) VALUES (3, '2023-07-25 03:57:38.110137', '2023-07-26 13:07:43.935304', 0, '冷宫奶团忙碌碌：这家没我得散！', '娘亲又去寻死上吊了！从小生活在冷宫中的阮阮操碎了心！她只是一溜出冷宫，就碰上了传言中的暴君爹，而且，一见面就要砍她脑袋QAQ好在人畜无害的小团子开始渐渐萌化了暴君爹爹的心……寻死的娘亲醒来后也性情大变，阮阮却发现自己竟能听到娘亲的心声——什么？娘亲又要和暴君爹爹和离？等等！娘亲又打算去暗杀暴君爹！阮阮好不容易安抚好了暴怒的爹爹，娘亲又开始闹腾了……忙活了半天，看着爹娘恩爱相拥的阮阮：“唉，这个家，没我得散啊！”', 2136, 78237, 434, 6, 38, 0, 'novels_image/3550182.jpg', 2, 2, 64);
INSERT INTO novel_station.novels_info_table (id, create_time, update_time, is_delete, title, description, watcher, words, collections, comments, chapters, is_end, image, author_info_id, novels_type_id, sales) VALUES (4, '2023-07-25 04:01:00.622003', '2023-07-25 04:01:00.622003', 0, '罗浮记', '蒋云昆章回体小说《罗浮记》，于2013年起笔，2023年6月写成，历时十年。是继《封神演义》《西游记》之后，中国第三部古典历史神话小说。上承《封神演义》，下启《西游记》，采取明清白话小说写法，以两晋演义为历史背景，演绎贾后乱政，八王之乱，五胡乱华，西晋灭，东晋立，道佛相争，葛洪奉太清道德天尊之命，下山辅佐晋元帝司马睿，衣冠南渡，延续华夏，扶助社稷，终于罗浮山寻八卦紫金炉，炼九转金丹，使天地神人鬼五界之仙金丹正果，定品列序，一统道教，传承人间的故事。整篇小说共一百回，83万字，恢弘大气，文采飞扬，诗词歌赋均是作者原创，其中英雄人物辈出，人间感悟通透，可谓中国文艺复兴的旷世之作。', 6321, 260488, 43141, 0, 32, 0, 'novels_image/3558307.jpg', 3, 2, 763);
INSERT INTO novel_station.novels_info_table (id, create_time, update_time, is_delete, title, description, watcher, words, collections, comments, chapters, is_end, image, author_info_id, novels_type_id, sales) VALUES (5, '2023-07-25 04:03:07.374262', '2023-07-25 04:03:07.374262', 0, '如梦起源', '一次偶然的相遇，命运从此改变，讲述不一样成长历程，有丰富多彩的剧情，跌宕起伏的人生，爆笑而严谨，闯女生更衣室、旅游掉进女生池塘、被一群基友大叔追的满街跑……领略不一样的玄幻之旅。', 664191, 848947, 634, 0, 743, 0, 'novels_image/3181084.png', 4, 1, 1597);
INSERT INTO novel_station.novels_info_table (id, create_time, update_time, is_delete, title, description, watcher, words, collections, comments, chapters, is_end, image, author_info_id, novels_type_id, sales) VALUES (6, '2023-07-25 04:05:26.134722', '2023-07-25 04:05:26.134722', 0, '同我穿越还有一本破书', '萧寒是一位年轻的刑警，在一次抓捕罪犯的过程中被狡猾的歹徒枪杀身亡，就此穿越到古代修仙世界，与他一同穿越的还有一本破书······       
