INSERT INTO novel_station.novels_info_table (id, create_time, update_time, is_delete, title, description, watcher, words, collections, comments, chapters, is_end, image, author_info_id, novels_type_id, sales) VALUES (8, '2023-07-25 05:34:15.191130', '2023-07-25 05:34:15.191130', 0, '青春荒唐我不负 你', '青春陪伴的是疼痛。你害怕身体的痛？还是心灵上的疼？深深浅浅的伤痕，刻在身上。手一触，便疼。后来。才发现，伤疤界算什么，有些故事，不用手去触碰，就痛得彻骨。是啊，伤疤难看又有什么关系，好比过内心千疮百孔，怎么愈合都于事无补。安洛以为，这一辈子除了风旧颜，再也遇不到深爱她的男孩。她是孤儿，无父无母。却意外收获平氏少爷平寂初的爱。他疼她，宠她。她却自卑地逃离，决定不爱上任何人。当爱情悄悄变了模样，她深爱的男孩风旧颜却一步步将她推向别人的怀抱谎言戳开，满目疮痍，回首往事才得知，原来你如此深爱……独家签约汤圆，不可转载，违者必究', 823, 165885, 1687, 0, 104, 0, 'novels_image/3468538.jpg', 7, 4, 6795);
